mapper.select:
select

from consume_info
where is_delete = 1

  AND user_id = #{userId}


  AND card_id = #{cardId}


  AND bank_id = #{bankId}


  AND `type` = #{type}


  AND `month` = #{month}

ORDER BY pay_time DESC


  LIMIT #{start},#{size}

select
  count(1)
from consume_info
where is_delete = 1

  AND user_id = #{userId}


  AND card_id = #{cardId}


  AND bank_id = #{bankId}


  AND `type` = #{type}


  AND `month` = #{month}
